import {UInt256Extract} from "../../../utils/types/UInt256Extract.sol";
import {UInt256Inject} from "../../../utils/types/UInt256Inject.sol";

contract UInt256BitsMock {
    using UInt256Extract for uint256;
    using UInt256Inject for uint256;

    function requireInjectExtractValueUnchanged(
        uint256 integer,
        uint256 value,
        uint8 length,
        uint8 index
    ) external pure {
        require(value == (integer.inject(value, length, index).extract(length, index)), "UInt256Bits: mismatch");
    }

    function extract(
        uint256 integer,
        uint8 length,
        uint8 index
    ) external pure returns (uint256) {
        return integer.extract(length, index);
    }

    function inject(
        uint256 integer,
        uint256 value,
        uint8 length,
        uint8 index
    ) external pure returns (uint256) {
        return integer.inject(value, length, index);
    }
}

contracts/utils/types/UInt256Extract.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity >=0.7.6 <0.8.0;

library UInt256Extract {
    /**
     * @dev extracts an integer value from certain bits in a uint256
     * @param integer uint256 the base number where to extract the value from.
     * @param length uint256 the number of bits of the value to decode.
     * @param index uint256 the index of the first bit in integer where the value is stored.
     * @return result uint256 the extracted value, shifted to the least significant bits.
     */
    function extract(
        uint256 integer,
        uint256 length,
        uint256 index
    ) internal pure returns (uint256) {
        require(length > 0, "UInt256Extract: length is zero");
        require(index + length <= 256, "UInt256Extract: out of bond");
        uint256 mask = (1 << length) - 1;
        return (integer >> index) & mask;
    }
}

